import { Component } from "react";

export default class extends React.Component {
  render() {
    return (
      <>
        <h1>hola mundo</h1>
        <p>Bienvenidos al curso de Next.js</p>
        <img src="platzi-logo.png" alt="platzi Logo" />
        <style jsx>{`
        h1 {
        color: red;
        }
        p {
          color: green;
        }
        img {
          max-width: 50%;
          display:block,
          margin: 0 auto;
        }
       `}</style>
       <style jsx global>{`
       body {
         background: white;
       }
       `}</style>

      </>
    )
  }
}
