  render(){

    return (
      <>
        <header><Link href="/"><a> Podcasts</a></Link></header>

    <style jsx>{`
        header {
          color: #fff;
          background: #8756ca;
          padding: 15px;
          text-align: center;
          text-decoration: none
        }
        header a {
          color: #fff
        }
    `}</style>
     <style jsx global>{`
        body {
          margin: 0;
          font-family: system-ui;
          background: white;
        }
      `}</style>

      </>
    )
  } 
}
